import React, { useState } from "react";


const Boton = ( {texto} ) => {

//establecemos un estado a la variable para que se muestren o no al pulsar
const [mostrarDatos, setMostrarDatos] = useState(false);

    const mostraDatosAPI = () =>{
         setMostrarDatos(!mostrarDatos);

    }




    return (

      <button 
      onClick={mostraDatosAPI}>Mostrar datos Api
      texto
      </button>

    );



}

export default Boton;
