import React, { Component } from 'react';

class Boton extends Component {

  constructor (props){
    super(props);
     this.state = {  //estado inicial antes de mostrarse en el DOM
      texto: '0',
    };
     console.log(texto,"soy constructor, antes de pintar ");
    }

  render() {

const {texto} = this.props; //para poder utilizar la prop sin referenciar this.props en el return

    return (

              <button>{texto}</button>  //definimos una prop que estará en un estado inicial '' y definiremos cuando llamemos al componente en un hijo
    );
  }
 }

export default Boton;
