import React, { Component } from 'react';
import Boton from './componentes/Boton';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { nuevotexto: 'Texto Inicial del Boton' };
  }

  cambiotexto = (event) => {
    this.setState({ nuevotexto: event.target.value }); //capturamos el texto y se lo pasamos a nuevotexto
  }

  render() {
    const textoreal = this.state.nuevotexto;

    return (
      <div className="App">
        <h3>Este es el componente</h3>
        <Boton texto={textoreal} /> {/**/}
        <input type="text" placeholder='Introduce un texto' onChange={this.cambiotexto} />
      </div>
    );
  }
}
