import React, { Component } from 'react';
import Boton from './componentes/Boton';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { textoreal: 'Texto Inicial del Boton' };
  }
  cambiotexto = (event) => {
    this.setState({ nuevotexto: event.target.value }); //capturamos el texto y se lo pasamos a nuevotexto
  }

  render() {


    const {cambiotexto} = this.props; //aunque sea una funcion para poder llamarlo en el return
    //como prop, sin this. la creo aqui

    return (
      <div className="App">
        <h3>Este es el componente</h3>
        <Boton texto={textoreal} /> {/*texto es la propiedad de capa arriba texto real definida aqui*/}
        <input type="text" placeholder='Introduce un texto' onChange={cambiotexto} /> {/**/}
      </div>
    );
  }
}
